package conversion;

public class DoubleToString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//valueOf
		double d=8.3;  
		
		String s=String.valueOf(d);  
		
		System.out.println(s);  
		
		//toString method
		double a=867.9;  
		
		String f =Double.toString(a);  
		
		System.out.println(f);  
		
		
		
		
		
	}

}
