
package important;

public class Pattern_Program3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		/*
		 * output : 3 2 1 
		 *          2 1 
		 *          1
		 * 
		 */
	
	
	      for (int i = 3; i >=1; i--) {
	    	  
	    	  for (int j = i; j >=1; j--) {
	    		  
	    		  System.out.print(j+" ");
				
			}

	    	  System.out.println();
		}
		
		
	
	
	
	}

}
