package important;

public class Pattern_Program4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i,j;
		
	
         for (i = 3; i >=1; i--) {
        	 for ( j = i; j >=1; j--) {
        		 
        		 System.out.print(j+" ");
				
			}
					
        	 System.out.println();
       }
	
	    for (i = 1; i <=3; i++) {
	    	
	    	for ( j = 1; j <= i; j++) {
	    		
	    		System.out.print(j+" ");
				
			}
			System.out.println();
		}
	
	
	
	
	
	
	
	}

}
