package interiewprogramms;

public class SwapTwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int nandhuSalary=15000;
		
		int thiyaguSalary=5000;
		
		System.out.println("Before Swapping"+ " nandhu :" +nandhuSalary +"thiyagu : "+ thiyaguSalary);
		
		int temp=nandhuSalary;
		
		nandhuSalary=thiyaguSalary;
		
		thiyaguSalary=temp;
		
		System.out.println("After Swapping"+ " nandhu :" +nandhuSalary +"thiyagu : "+ thiyaguSalary);
					

		int first=50; int second=1000;
		
		first=first-second;
		
		second=first+second;
		
		first=second-first;
		
		System.out.println("After swapping"+"first :"+first +"second: "+ second);
		
	
	

	
	
	}

}
